import { Card } from 'react-bootstrap'
import { Link } from 'react-router-dom'

function Post({ post }) {
  return (

    <Card>
        <Link to={`/product/${post._id}`}>
          <Card.Img src={post.image}/>
        </Link>



    </Card>


  )
}

export default Post
