import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import {Form, Button, Row, Col } from 'react-bootstrap'
import {useDispatch, useSelector} from 'react-redux'
import Loader from '../components/Loader'
import Message from '../components/Message'
import FormContainer from '../components/FormContainer'
import { getUserDetails, updateUserProfile } from '../actions/userActions'

function ProfileScreen( history) {

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')      
    const [message, setMessage] = useState('')

    const dispatch = useDispatch()

    const userDetails = useSelector(state => state.userDetails) 
    const {error, loading, user} = userDetails //prendo le informazioni dell'utente dal reducer nello store

    const userLogin = useSelector(state => state.userLogin) 
    const { userInfo } = userLogin

    useEffect(() => {      
      if (!userInfo) {
        history.push('/login') 
      }else{
        if(!user || !user.name){
            dispatch(getUserDetails('profile')) //riempie il parametro id nella ACTION
        } else {
            setName(user.name)
            setEmail(user.email)
        }
      }
    }, [dispatch, history, userInfo, user])


    const submitHandler = (e) => {
       e.preventDefault()

       if(password !== confirmPassword){
         setMessage('Passwords do not match')
       } else {
         console.log('Updating...')
       }
       /* console.log(email, password, dispatch)
       console.log(typeof email, typeof password) */
    } 



  return (
    <Row>
        <Col md={3}>
            <h2>User Profile</h2>

            <Form onSubmit={submitHandler}>

          <Form.Group controlId='name'>
            <Form.Label>Name</Form.Label>
              <Form.Control
              required
              type='name'
              placeholder='Enter name'  //L'immagine risulta prepopolata per via dell'USEEFFECT che carica i valori dell'utente 
              value={name}
              onChange={(e) => setName(e.target.value)}
              className='my-3'
              ></Form.Control>
          </Form.Group>

          <Form.Group controlId='email'>
            <Form.Label>Email Address</Form.Label>
            <Form.Control 
              required
              type='email'
              placeholder='Enter Email'
              value ={email}
              onChange={(e) => setEmail(e.target.value)} //ogni volta che il valore cambia, cambia anche il valore dell'useState 
              className='my-3'
            ></Form.Control>
          </Form.Group>

          <Form.Group>
            <Form.Label>Password </Form.Label>
            <Form.Control

              type='password'
              placeholder='Enter Password'
              value ={password}
              onChange={(e) => setPassword(e.target.value)} 
              className='my-3'
            ></Form.Control>
          </Form.Group>

          <Form.Group>
            <Form.Label>Confirm Password </Form.Label>
            <Form.Control
              /* required */ //non ho bisogno del passwordField required
              type='password'
              placeholder='Confirm Password'
              value ={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)} 
              className='my-3'
            ></Form.Control>
          </Form.Group>

          <Button type='submit' variant='primary' className='my-3' >Update</Button>
       </Form>

        </Col>

        <Col md={9}>
           <h2>My Orders</h2> 
       </Col>
    </Row>
  )
}

export default ProfileScreen
