import React,{useEffect, useCallback} from 'react'
import {Container ,Row, Col, ListGroup, Image, Form, Button, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import ProductCarousel from '../components/ProductCarousel'
import AnimatedPage from '../components/AnimatedPage'
import { listPosts } from '../actions/postActions'
import Loader from '../components/Loader'
import Post from '../components/Post'
import Message from '../components/Message'


function HomeScreen() {

  const dispatch = useDispatch();
  const postList = useSelector(state => state.postList)
  const {error, loading, posts} = postList 

  const basicMasonry = useCallback(() => {
    const numCols = 3;
    const colHeights = Array(numCols).fill(0);
    const container = document.querySelector('#masonry-with-columns');
  },[]);

  useEffect(() => {
    dispatch(listPosts);
  }, [dispatch])




  return (
  <AnimatedPage>
     <div>
    {loading ? <Loader/>
          : error ? <Message variant='danger' >{error}</Message>

            :
          <div className='masonry-with-columns' id='masonry-with-columns'>
            <Row> 
            {posts.map(post => (
                <Col key={post._id}  xs={6} sm={6} md={6} lg={4} xl={3}>
                    <Post post={post}/>
                </Col>

                ))}
            </Row>
          </div> 
          }
    </div> 
  </AnimatedPage>
  )
}

export default HomeScreen
