export const productListReducers = (state = {products: [] }, action) => { //all'inizio solo state viene renderizzato
    switch (action.type) {
        case 'PRODUCT_LIST_REQUEST':  //mentre i prodotti stanno caricando/aggiornando
            return { loading: true, products: [] }

        case 'PRODUCT_LIST_SUCCESS':
            return { loading: false, products: action.payload} //il nostro state avra una lista prodotti quando sara SUCCESSFULL

        case 'PRODUCT_LIST_FAIL':
            return { loading: false, error: action.payload }

        default:
            return state
    }

}
