import React, {useEffect} from 'react'
import {Link} from 'react-router-dom'
import {useDispatch, useSelector} from 'react-redux'
import {Row, Col, ListGroup, Image, Form, Button, Card } from 'react-bootstrap'
import Message  from '../components/Message'
import {addToCart} from '../actions/cartActions'


function CartScreen({ match, location, history }) {
  const productId = match.params.id
  const qty = location.search ? Number(location.search.split('=')[1]): 1 //prende il valore di qty dall'URL
                                                                          //[1] Number colpishe solo il secondo indice quindi 1
  const dispatch = useDispatch()

  const cart = useSelector(state=> state.cart)
  const { cartItems } = cart

  console.log('cartItems:', cartItems)


  useEffect(() =>{
    if(productId){
      //con dispatch mando le azioni al reducer
        dispatch(addToCart(productId,qty))
    }
  }, [dispatch, productId, qty])

  return (
    <Row>
        <Col md={8}>
            <h1>Shopping Cart</h1>
            {cartItems.length === 0 ? (
              <Message variant='info'>Your cart is empty <Link to='/'>Go back</Link></Message>
            ) : (
              <ListGroup variant='flush'>
                  {cartItems.map(item => (
                    <ListGroup.item key={item.product}>
                      <Row>
                          <Col md={2}>
                            <Image src={item.image} alt={item.name} fluid rounded></Image>
                          </Col>

                          <Col md={3}>
                            <Link to={`/product/${item.product}`} >{item.name}</Link>
                          </Col>

                          <Col md={2}>
                            ${item.price}
                          </Col>
                      </Row>
                    </ListGroup.item>
                  ))}
              </ListGroup>
            ) }
        </Col>

        <Col md={4}>

        </Col>
    </Row>
  )
}

export default CartScreen
