import React from 'react';
import { Container } from 'react-bootstrap'
import Footer from './components/Footer'
import Header from './components/Header'
import { BrowserRouter as Router, Route} from 'react-router-dom'
import { translate } from 'libretranslate';

import { useTranslation } from 'react-i18next';

import HomeScreen from './screens/HomeScreen'
import ProductScreen from './screens/ProductScreen'
import CartScreen from './screens/CartScreen'
import LoginScreen from './screens/LoginScreen' 
import RegisterScreen from './screens/RegisterScreen'
import ProfileScreen from './screens/ProfileScreen' 
import ShippingScreen from './screens/ShippingScreen'  
import PaymentScreen from './screens/PaymentScreen'
import PlaceOrderScreen from './screens/PlaceOrderScreen'
import OrderScreen from './screens/OrderScreen'
import TransitionScreen from './screens/TransitionScreen'
import UserListScreen from './screens/UserListScreen'
import UserEditScreen from './screens/UserEditScreen'
import ProductListScreen from './screens/ProductListScreen'
import ProductEditScreen from './screens/ProductEditScreen'
import OrderListScreen from './screens/OrderListScreen'






function App() {
  const { i18n } = useTranslation();

  const translateText = async (text, targetLanguage) => {
    try {
      const translations = await translate({
        q: text,
        source: 'en',
        target: targetLanguage,
      });

      return translations[0].translatedText;
    } catch (error) {
      console.error('Errore durante la traduzione del testo:', error);
      return text; // Restituisce il testo originale se si verifica un errore
    }
  };

  const translateComponentTexts = async (component) => {
    const translatedComponent = { ...component };

    for (const key in component) {
      if (typeof component[key] === 'string') {
        const targetLanguage = 'it';
        translatedComponent[key] = await translateText(component[key], targetLanguage/* i18n.language */);
      }
    }

    return translatedComponent;
  };

  const TranslatedHomeScreen = React.lazy(() => import('./screens/HomeScreen').then((module) => {
    const component = module.default;
    return translateComponentTexts(component);
  }));
  // Importa gli altri componenti dello schermo tradotti qui


  return (
    <Router>
      <Header />
      <main className="py-3">
        <Container>

              <Route path='/' component={TranslatedHomeScreen} exact/>
              <Route path='/login' component={LoginScreen} />
              <Route path='/register' component={RegisterScreen} />
              <Route path='/profile' component={ProfileScreen} />
              <Route path='/shipping' component={ShippingScreen}/>
              <Route path='/payment' component={PaymentScreen}/>
              <Route path='/placeorder' component={PlaceOrderScreen}/>
              <Route path='/order/:id' component={OrderScreen}/>
              <Route path='/transition' component={TransitionScreen}/>
              <Route path='/product/:id' component={ProductScreen} />
              <Route path='/cart/:id?' component={CartScreen}/>

              <Route path='/admin/userlist' component={UserListScreen}/>
              <Route path='/admin/user/:id/edit' component={UserEditScreen}/>

              <Route path='/admin/productlist' component={ProductListScreen}/>
              <Route path='/admin/product/:id/edit' component={ProductEditScreen}/>

              <Route path='/admin/orderlist' component={OrderListScreen}/>

        </Container>
      </main>
      <Footer />
    </Router>
  );
}

export default App;
