import {React, useState} from 'react'
import { Row,Col,Card,Button,Modal,Image } from 'react-bootstrap'
import { Link } from 'react-router-dom'

function Post({ post }) {

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  };


  return (
    <div>
    <Card>
          <Card.Img src={post.image} onClick={handleShow}/>
    </Card>

    <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{post.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Image src={post.image} style={{width:'420px', borderRadius:'10px'}}></Image>
        </Modal.Body>
        <Modal.Footer style={{containerStyle}}>
          <p >{post.description}</p>
        </Modal.Footer>
      </Modal>
    </div>

  )
}

export default Post
