import axios from 'axios'
import {
    PRODUCT_LIST_REQUEST,
    PRODUCT_LIST_SUCCESS,
    PRODUCT_LIST_FAIL,

    PRODUCT_DETAILS_REQUEST,
    PRODUCT_DETAILS_SUCCESS,
    PRODUCT_DETAILS_FAIL,

    PRODUCT_DELETE_REQUEST,
    PRODUCT_DELETE_SUCCESS,
    PRODUCT_DELETE_FAIL,
} from '../constants/productConstants'


export const listProducts = () => async (dispatch) => { 
    try{

        dispatch({type:PRODUCT_LIST_REQUEST}) //request e parte la ruota di caricamento

        const { data } = await axios.get('/api/products/') //check

        dispatch({
            type:PRODUCT_LIST_SUCCESS, //i dati sono arrivati con successo
            payload:data})

    } catch(error) {

        dispatch({
            type: PRODUCT_LIST_FAIL,
            payload: error.response && error.response.data.detail //
            ? error.response.data.detail
            : error.message
        })


    }
}


export const listProductDetails = (id) => async (dispatch) => { 
    try{

        dispatch({type:PRODUCT_DETAILS_REQUEST})

        const { data } = await axios.get(`/api/products/${id}`)//id viene passata da frontend

        dispatch({
            type:PRODUCT_DETAILS_SUCCESS,
            payload:data})

    } catch(error) {

        dispatch({
            type: PRODUCT_DETAILS_FAIL,
            payload: error.response && error.response.data.detail 
            ? error.response.data.detail
            : error.message
        })


    }
}

export const deleteProduct = (id) => async (dispatch, getState) => {
    console.log(id)
    try{

        dispatch({
            type:PRODUCT_DELETE_REQUEST
        })

        const {
            userLogin: { userInfo }, 
        } = getState()

        const config = { 
            headers: {      
                'ContentType' : 'application/json',
                Authorization: `Bearer ${userInfo.token}`   
            }                                            
        }                                                

        const {data} = await axios.get(
            `/api/orders/myorders/`,                
             config 
        )

       dispatch({
            type:PRODUCT_DELETE_SUCCESS,
            payload:data//messaggio della response di Django
       })



    }catch(error) {

        dispatch({
            type: PRODUCT_DELETE_FAIL,
            payload: error.response && error.response.data.detail 
            ? error.response.data.detail
            : error.message
        })
    }

}
