 import {
USER_LOGIN_REQUEST,
USER_LOGIN_SUCCESS,
USER_LOGIN_FAIL,

USER_LOGOUT,

USER_REGISTER_REQUEST,
USER_REGISTER_SUCCESS,
USER_REGISTER_FAIL,

USER_DETAILS_REQUEST,
USER_DETAILS_SUCCESS,
USER_DETAILS_FAIL,

USER_UPDATE_PROFILE_REQUEST,
USER_UPDATE_PROFILE_SUCCESS,
USER_UPDATE_PROFILE_FAIL,
} from '../constants/userConstants'

//E' GRAZIE AL REDUCER SE POSSO VEDERE IL VALORE NELLA SEZIONE REDUX DEL BROWSER
export const userLoginReducer = (state = {}, action) => { 
    switch (action.type) {
        case USER_LOGIN_REQUEST:  //mentre i prodotti stanno caricando/aggiornando
            return { loading: true }

        case USER_LOGIN_SUCCESS:
            return { loading: false, userInfo: action.payload} //dall'action vengono presi i product

        case USER_LOGIN_FAIL:
            return { loading: false, error: action.payload }

        case USER_LOGOUT:
            return {}//state vuoto

        default:
            return state
    }

} 



export const userRegisterReducer = (state = {}, action) => { 
    switch (action.type) {
        case USER_REGISTER_REQUEST:  //mentre i prodotti stanno caricando/aggiornando
            return { loading: true }

        case USER_REGISTER_SUCCESS:
            return { loading: false, userInfo: action.payload} //dall'action vengono presi i product

        case USER_REGISTER_FAIL:
            return { loading: false, error: action.payload }

        case USER_LOGOUT:
            return {}//state vuoto

        default:
            return state
    }

} 



export const userDetailReducer = (state = {user: {}}, action) => { 
    switch (action.type) {
        case USER_DETAILS_REQUEST:  //mentre i prodotti stanno caricando/aggiornando
            return { ...state, loading: true }

        case USER_DETAILS_SUCCESS:
            return { loading: false, user: action.payload} //dall'action vengono presi i product

        case USER_DETAILS_FAIL:
            return { loading: false, error: action.payload }


        default:
            return state
    }

} 



export const userUpdateProfileReducer = (state = {user: {}}, action) => { 
    switch (action.type) {
        case USER_UPDATE_PROFILE_REQUEST:  
            return { ...state, loading: true }

        case USER_UPDATE_PROFILE_SUCCESS:
            return { loading: false, success:true, userInfo: action.payload} 

        case USER_UPDATE_PROFILE_FAIL:
            return { loading: false, error: action.payload }


        default:
            return state
    }

}
