import React,{useEffect} from 'react'
import {Container ,Row, Col, ListGroup, Image, Form, Button, Card } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import ProductCarousel from '../components/ProductCarousel'
import AnimatedPage from '../components/AnimatedPage'
import { listPosts } from '../actions/postActions'
import Loader from '../components/Loader'
import Post from '../components/Post'
import Message from '../components/Message'


function HomeScreen() {

  const dispatch = useDispatch();
  const postList = useSelector(state => state.postList)
  const {error, loading, posts} = postList 

  useEffect(() => {
    dispatch(listPosts);
  }, [dispatch])

  const getRandomNumber = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
  };

  const getRandomShape = () => {
    const minWidth = 50;
    const maxWidth = 150;
    const minHeight = 50;
    const maxHeight = 150;

    return {width: getRandomNumber(minWidth, maxWidth),
    height: getRandomNumber(minHeight, maxHeight)}
  };


  return (
  <AnimatedPage>
   {/*  <div>
    {loading ? <Loader/>
          : error ? <Message variant='danger' >{error}</Message>

            :
          <div>
            <Row> 
            {posts.map(post => (
                <Col key={post._id} sm={12} md={6} lg={4} xl={3}>
                    <Post  post={post}/>
                </Col>

                ))}
            </Row>
          </div> 
          }
    </div> */}
    <h1>HEllo</h1>
  </AnimatedPage>
  )
}

export default HomeScreen
