import React, {useState, useEffect} from 'react'
import {Link} from 'react-router-dom'
import {Form, Button } from 'react-bootstrap'
import {useDispatch, useSelector} from 'react-redux'
import Loader from '../components/Loader'
import Message from '../components/Message'
import FormContainer from '../components/FormContainer'
import { listProductDetails } from '../actions/productActions'
import { USER_UPDATE_RESET } from '../constants/userConstants'

function ProductEditScreen({match, history}) {
  //
    const productId = match.params.id


    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [image, setImage] = useState('')
    const [brand, setBrand] = useState('')
    const [category, setCategory] = useState('')
    const [countInStock, setCountInStock] = useState(0)
    const [description, setDescription] = useState('')
    //

    const dispatch = useDispatch()

    const productDetails = useSelector(state => state.productDetails) 
    const { error, loading, product } = productDetails
    //

    useEffect(() => { 

        if (!product.name || product._id !== Number(productId)){
          dispatch(listProductDetails(productId))
        }else{
          setName(product.name)
          setPrice(product.price)
          setImage(product.image)
          setBrand(product.brand)
          setCategory(product.category)
          setCountInStock(product.countInStock)
          setDescription(product.description)
        }

    }, [dispatch, product, productId, history])


    const submitHandler = (e) => {
       e.preventDefault()      

    } 
  return (

    <div>

      <Link to='/admin/productList'>
        Go Back
      </Link>


      <FormContainer>
       <h1>Edit Product</h1>


      {loading ? <Loader/> : 
       error ? <Message variant='danger'>{error}</Message>  : 
     (
            <Form onSubmit={submitHandler}>

            <Form.Group controlId='name'>
              <Form.Label>Name</Form.Label>
                <Form.Control
                type='name'
                placeholder='Enter name'
                value={name}
                onChange={(e) => setName(e.target.value)}
                className='my-3'
                ></Form.Control>
            </Form.Group>

            <Form.Group controlId='email'>
              <Form.Label>Email Address</Form.Label>
              <Form.Control 
                required
                type='email'
                placeholder='Enter Email'
                value ={email}
                onChange={(e) => setEmail(e.target.value)} //ogni volta che il valore cambia, cambia anche il valore dell'useState 
                className='my-3'
              ></Form.Control>
            </Form.Group>

            <Form.Group controlId='isAdmin'>
              <Form.Check
                type='checkbox'
                label='Is Admin'
                checked={isAdmin}
                onChange={(e) => setIsAdmin(e.target.checked)} //ogni volta che il valore cambia, cambia anche il valore dell'useState 
                className='my-3'
              ></Form.Check>
            </Form.Group>

            <Button type='submit' variant='primary'>Update</Button>
        </Form>

      )}




    </FormContainer>
    </div>

  )
}

export default ProductEditScreen
